mapper.select:
select stillcut_filename 
	from movie_stillcut
	where movie_id =#{movie_id}

select b.* from ( select a.*,rownum as rnum from (
select * from movie order by movie_release_date DESC
select b.movie_name,b.movie_release_date,b.movie_ko_name,b.movie_id from movie b , ( select a.movie_id, NVL(avg(b.movie_review_rate),0) as 별점 from movie a left outer join movie_review b on (b.movie_id = a.movie_id) group by a.movie_id ) a where b.movie_id = a.movie_id and b.movie_show_yn = 'Y' order by a.별점 desc
select h.movie_id,h.movie_name,h.movie_ko_name,h.movie_release_date from ( select f.movie_id, NVL(sum(e.좌석소계),0) as 예매수 from movie f left join ( select j.movie_id, count(c.sit_num)as 좌석소계 from reservation_sit c,movie_reservation d,movie_show_info j where c.reservation_id = d.reservation_id and j.show_id = c.show_id and d.pay_id is not null group by j.movie_id ) e on(f.movie_id= e.movie_id) group by f.movie_id ) g ,movie h where g.movie_id = h.movie_id order by g.예매수 desc
) a )b where rnum between #{startRow} and #{endRow}
