mapper.select:
select a.*, rownum as rnum from ( SELECT snack_id, snack_type, snack_combo_yn, snack_name, snack_subname, snack_price, snack_size, snack_origin, snack_mat, snack_info, snack_exp, snack_change_info, snack_use_info
FROM snack
where snack_type=#{snack_type}
) a where rownum between #{startRow} and #{endRow}

SELECT a.snack_buy_id, a.snack_id, a.mem_id, b.mem_name as mem_name, c.snack_name as snack_name, a.snack_cnt, a.pay_id, d.pay_addpoint as mem_point, d.pay_cancel_yn as pay_canel_yn, d.pay_amount as pay_amount, a.snack_use_yn, d.pay_date as pay_date FROM snack_buy a, member b, snack c, pay d WHERE a.mem_id = b.mem_id AND a.snack_id = c.snack_id AND a.pay_id = d.pay_id AND a.pay_id is not null AND d.pay_cancel_yn = 'N'
AND b.mem_name = #{searchWord}
AND c.snack_name LIKE '%' || #{searchWord} || '%'
AND a.mem_id = #{mem_id}
ORDER BY a.snack_buy_id desc

SELECT count(*) as count FROM snack_buy a, member b, snack c, pay d WHERE a.mem_id = b.mem_id AND a.snack_id = c.snack_id AND a.pay_id = d.pay_id AND a.pay_id is not null AND d.pay_cancel_yn = 'N'
AND b.mem_name = #{searchWord}
AND c.snack_name LIKE '%' || #{searchWord} || '%'
AND a.mem_id = #{mem_id}

mapper.update:
UPDATE member SET			
    mem_point = mem_point + #{mem_point}
WHERE mem_id=#{mem_id}
